package week1.day1;

public class LearnSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String subject = "Tamil";
		
		switch(subject)
		{
		case("English"):System.out.println("English");break;
		case("Tamil"): System.out.println("Tamil");break;
		default:
			System.out.println("No such subject");break;
		}
	}

}
